const cpfMask=e=>e.replace(/\D/g,"").replace(/(\d{3})(\d)/,"$1.$2").replace(/(\d{3})(\d)/,"$1.$2").replace(/(\d{3})(\d{1,2})/,"$1-$2").substring(0,14),phoneMask=e=>e.replace(/\D/g,"").replace(/(\d{2})(\d)/,"($1) $2").replace(/(\d{5})(\d)/,"$1-$2").substring(0,15),cepMask=e=>e.replace(/\D/g,"").replace(/(\d{5})(\d)/,"$1-$2").substring(0,9),applyMask=(e,a)=>{e&&e.addEventListener("input",e=>{e.target.value=a(e.target.value)})};export function applyMasks(){const e=document.getElementById("cpf"),a=document.getElementById("telefone"),p=document.getElementById("cep");applyMask(e,cpfMask),applyMask(a,phoneMask),applyMask(p,cepMask)}